mapper.update:

update dev_apply
set devId = #{devId,jdbcType=INTEGER},
  update_time = now(),
  `status` = #{status,jdbcType=INTEGER},
  applyInfo = #{applyInfo,jdbcType=LONGVARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:

select id, devId, create_time, update_time, `status`, applyInfo
from dev_apply
where id = #{id,jdbcType=INTEGER}

select dev_apply.id as 'id' ,dev_apply.update_time as modifyDate ,dev_apply.applyInfo as applyInfo ,app_status_status.valueName as 'statusName' ,app_status_status.id as 'status' ,dev_user.devName as devName from dev_apply left join app_status as app_status_status on dev_apply.status= app_status_status.id left join dev_user on dev_apply.devId=dev_user.id
and dev_apply.devId = ${params.queryDid}
order by dev_apply.update_time desc
